import React, {Component} from "react";
// import axios from "axios";
import "./Home.css"
import SearchBox from "../../components/SearchBox"
import SearchResult from "../../components/SearchResult/SearchResult";



class Home extends Component {
    state ={
        userInput:"",
        results:"",
        wordArr:[]
    }
    //method to handle/track the form input
    handleInputChange = event => {
        this.setState ({userInput: event.target.value})
    };
    translate = (str)=> {
        str=str.toLowerCase();

        // for words that start with a vowel:
        if (["a", "e", "i", "o", "u"].indexOf(str[0]) > -1) {
            return str=str+"ay";
        }

        // for words that start with one or more consonants
       else {
       //check for multiple consonants
           for (var i = 0; i<str.length; i++){
               if (["a", "e", "i", "o", "u"].indexOf(str[i]) > -1){
                   var firstconstant = str.slice(0, i);
                   var middle = str.slice(i, str.length);
                   str = middle+firstconstant+"ay";
                 break;
               }
            }
        return str;
       }
    }

    //method for submit button
    handleFormSubmit = event => {
        event.preventDefault();
        console.log('calling the API')

        // Making the call to external API to get the photos:
        // axios.get(`https://robohash.org/${this.state.userInput}?set=set2`)
        //     .then(res => {
        //         console.log('result',res)
        //         this.setState({results:res.config.url})
        //         this.state.wordArr.push(this.state.results);
        //         console.log("robot array:", this.state.wordArr)

        // }) 
        .catch(error => console.log(error));      
    }

    render(){
        return (
            <container className="container-fluid">
                <div style={{textAlign:"center", marginTop:"150px"}}>
                    <h1 className="typewriter">Welcome to the pig latin translator App!</h1>
                    <br/>
                    <h2>let's learn some new words</h2>
                    <br/>
                    <SearchBox
                    handleInputChange={this.handleInputChange}
                    handleFormSubmit={this.handleFormSubmit}
                    />
                    <SearchResult
                    results={this.state.results}
                    />
                    <div>
                    {this.state.wordArr.map(i => 
                    <img style={{width:"65px",height:"65px", marginRight:"10px"}} 
                    src={i} alt=""/>)}                       
                    </div>

                </div>
            </container>
        )
    }
}



export default Home;
